'use strict';var Codeview=function(){var runCodeView=function(){var bsElement=$(".code-example");if(bsElement.length){$.ajaxSetup({cache:true});var modalSource='<div class="modal fade" id="source-modal" tabindex="-1" role="dialog">  <div class="modal-dialog modal-lg"> <div class="modal-content"> <div class="modal-header"> <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button> <h4 class="modal-title" id="myModalLabel">Source Code HTML</h4> </div> <div class="modal-body"> <pre class="line-numbers"> <code id="copy-code" class="language-html"></code> </pre> </div> <div class="modal-footer"> <button type="button" class="btn-e btn-e-lg btn-e-dark rounded btn-copy" data-clipboard-action="copy" data-clipboard-target="#copy-code" data-dismiss="modal">CODE COPY</button> </div> </div> </div> </div> </div>';$(modalSource).appendTo('body');var codeBtn=$("<div id='source-button' class='btn-e btn-e-purple btn-e-xs' style='padding-left:15px;padding-right:15px'><i class='fa fa-code'></i></div>");codeBtn.click(function(){var html=$(this).parent().html();html=cleanSource(html);$("#source-modal code").text(html);$("#source-modal").modal();var source=$("#source-modal").find('code');setTimeout(function(){source.each(function(i,block){Prism.highlightElement(block)})},250);$('.btn-clipboard').on('click',function(){var selection=$(this).parents('.modal-dialog').find('code');selection.selectText()});$(document).keypress(function(e){if(e.which==99){console.log('go');$('.btn-clipboard').click()}})});bsElement.hover(function(){$(this).append(codeBtn);codeBtn.show()},function(){codeBtn.hide()});var sourceBtn=$('.btn-e-source, .btn-e-document');sourceBtn.click(function(){$('.btn-clipboard').on('click',function(){var selection=$(this).parents('.modal-dialog').find('code');selection.selectText()})});$('.source-example').hover(function(){$(this).find('.btn-e-source').addClass('btn-visible')},function(){$(this).find('.btn-e-source').removeClass('btn-visible')});var cleanSource=function(html){var lines=html.split(/\n/);lines.shift();lines.splice(-1,1);var indentSize=lines[0].length-lines[0].trim().length;lines=lines.map(function(line){line=line.substring(indentSize);return line});lines=lines.join("\n");return lines};jQuery.fn.selectText=function(){var doc=document,element=this[0],range,selection;if(doc.body.createTextRange){range=document.body.createTextRange();range.moveToElementText(element);range.select()}else if(window.getSelection){selection=window.getSelection();range=document.createRange();range.selectNodeContents(element);selection.removeAllRanges();selection.addRange(range)}}}};return{init:function(){runCodeView()}}}();