var isFitting = false;
var t_codi = localStorage.getItem("temp_codi") ||{'lowerid' : '0000', 'upperid': '0000'};

function sleep(ms){
    ts1 = new Date().getTime() + ms;
    do ts2 = new Date().getTime(); while (ts2<ts1);
}

function isEmpty(obj) {
  for(var prop in obj) {
      if(obj.hasOwnProperty(prop))
          return false;
  }

  return JSON.stringify(obj) === JSON.stringify({});
}

async function request_fitting(it_id, ca_id){
    if(!it_id) {
        alert("코드가 올바르지 않습니다.");
        return 0;
    }

    switch(ca_id){
        case 1001:
        case 1002:
        case 1003:
            t_codi['lowerid'] = it_id.toString();
        break;
        case 1101:
            t_codi['upperid'] = it_id.toString();
    }

    $.post("/shop/fitting_request.php", { it_id : JSON.stringify(t_codi)}, function(res) {
        console.log(res);
        sleep(4000);
        var result = JSON.parse(res);
        $('#loader').hide();  
        if(result['result']){
            // var src = result['result'];
            var hash = user_id + t_codi['upperid'] + t_codi['lowerid'];
            console.log(hash);
            var src = "/res/" + user_id + "/" + MD5(hash) + ".png";
            t_codi['src'] = src;
            console.log(src);
            isFitting = false;
            localStorage.setItem("temp_codi", JSON.stringify(t_codi));
            return result;
        }
        return 0;
    });

    return true;
}

